using BenchmarkDotNet.Attributes;
using BenchmarkDotNet.Order;
using Mittons.DiffMatchPatch.Benchmark.Original;
using Mittons.DiffMatchPatch.Extensions;

namespace Mittons.DiffMatchPatch.Benchmark.CommonMatches;

[MemoryDiagnoser]
[Orderer(SummaryOrderPolicy.FastestToSlowest)]
[RankColumn]
public class CommonSuffixBenchmarks
{
    public static readonly (string left, string right)[] TestCases = [
        ("abc", "xyz"),
        ("1234abcdef", "1234xyz"),
        ("1234", "1234xyz"),
        ("hello world", "hello universe"),
        ("abcdefg", "abcxyz"),
        ("", ""),

        ("abc", "xyz"),
        ("abcdef1234", "xyz1234"),
        ("1234", "xyz1234"),
        ("world hello", "universe hello"),
        ("defgabc", "xyzabc"),
        ("", ""),

        ("1234567890", "abcdef"),
        ("12345", "23"),
        ("1234567890", "a345678z"),
        ("a345678z", "1234567890"),
        ("abc56789z", "1234567890"),
        ("a23456xyz", "1234567890"),
        ("121231234123451234123121", "a1234123451234z"),
        ("x-=-=-=-=-=-=-=-=-=-=-=-=", "xx-=-=-=-=-=-=-="),
        ("-=-=-=-=-=-=-=-=-=-=-=-=y", "-=-=-=-=-=-=-=yy"),
        ("qHilloHelloHew", "xHelloHeHulloy"),
        ("", ""),

        ("", "abcd"),
        ("123456", "abcd"),
        ("abc", "abcd"),
        ("123456xxx", "xxxabcd"),
        ("fi", "\ufb01i"),
    ];

    public static diff_match_patch OriginalDiffMatchPatch = new();

    public static List<(bool hasCommonSuffix, string commonSuffix, int commonSuffixLength)> LegacyApproach()
    {
        List<(bool hasCommonSuffix, string commonSuffix, int commonSuffixLength)> results = [];
        foreach (var (left, right) in TestCases)
        {
            var commonSuffixLength = OriginalDiffMatchPatch.diff_commonSuffix(left, right);
            var hasCommonSuffix = commonSuffixLength > 0;
            var commonSuffix = left[^commonSuffixLength..];
            results.Add((hasCommonSuffix, commonSuffix, commonSuffixLength));
        }

        return results;
    }

    public static List<(bool hasCommonSuffix, int commonSuffixLength)> NewApproach()
    {
        List<(bool hasCommonSuffix, int commonSuffixLength)> results = [];
        foreach (var (left, right) in TestCases)
        {
            var hasCommonSuffix = left.TryFindCommonSuffix(right, out var commonSuffix);
            var commonSuffixLength = commonSuffix.Length;
            results.Add((hasCommonSuffix, commonSuffixLength));
        }

        return results;
    }

    [Benchmark]
    public void BenchmarkLegacy()
    {
        LegacyApproach();
    }

    [Benchmark]
    public void BenchmarkNew()
    {
        NewApproach();
    }
}
